import React, { useState } from "react";
import { Link } from "react-router-dom";
import { BlogData } from "../../Data/BlogData";
import Card from "./Card";

export default function BlogSection() {
  const [blog, setBlog] = useState(BlogData);
  const categories = [...new Set(BlogData.map((Val) => Val.category1))];
  const filterItem = (curcat) => {
    console.log(curcat);
    const newBlog = BlogData.filter((newVal) => {
      return newVal.category === curcat;
      // comparing category for displaying data
    });
    setBlog(newBlog);
    console.log(blog);
  };
  return (
    <div>
      <nav aria-label="breadcrumb" className="breadcrumb-nav mb-2">
        <div className="container another-container">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <a href="/">Home</a>
            </li>
            <li className="breadcrumb-item">
              <a href="/blog">Blog</a>
            </li>
          </ol>
        </div>
        {/* End .container */}
      </nav>
      {/* End .breadcrumb-nav */}
      <div className="container another-container">
        <nav className="blog-nav">
          <ul className="menu-cat entry-filter justify-content-center">
            <li className="active">
              <a href="#" onClick={() => setBlog(BlogData)}>
                All Blog Posts<span>9</span>
              </a>
            </li>
            {categories.map((Val, id) => {
              return (
                <li>
                  <a href="#" key={id} onClick={() => filterItem(Val)}>
                    {Val}
                  </a>
                </li>
              );
            })}
            {/* <li></li> */}
            {/* <li>
              <a href="#" data-filter=".shopping">
                Shopping<span>1</span>
              </a>
            </li>
            <li>
              <a href="#" data-filter=".fashion">
                Fashion<span>2</span>
              </a>
            </li>
            <li>
              <a href="#" data-filter=".travel">
                Travel<span>4</span>
              </a>
            </li>
            <li>
              <a href="#" data-filter=".hobbies">
                Hobbies<span>2</span>
              </a>
            </li> */}
          </ul>
          {/* End .blog-menu */}
        </nav>
        {/* End .blog-nav */}
        <div className="entry-container max-col-3" data-layout="fitRows">
          {/* <Card data={blog} /> */}
          {blog.map((item, index) => {
            return (
              <div
                className={`entry-item ${item.category1} ${item.category2} col-sm-6 col-lg-4`}
                key={index}
              >
                <article className="entry entry-grid text-center">
                  <figure className="entry-media">
                    <div
                      className="owl-carousel owl-simple owl-light owl-nav-inside"
                      data-toggle="owl"
                    >
                      <a href="single.html">
                        <img src={item.img1} alt="image desc" />
                      </a>
                      <a href="single.html">
                        <img src={item.img2} alt="image desc" />
                      </a>
                    </div>
                    {/* End .owl-carousel */}
                  </figure>
                  {/* End .entry-media */}
                  <div className="entry-body">
                    <div className="entry-meta">
                      <span className="entry-author">
                        by <a href="#">{item.author}</a>
                      </span>
                      <span className="meta-separator">|</span>
                      <a href="#">{item.Date}</a>
                      <span className="meta-separator">|</span>
                      <a href="#">{item.comments} Comments</a>
                    </div>
                    {/* End .entry-meta */}
                    <h2 className="entry-title">
                      <a href="single.html">{item.title}</a>
                    </h2>
                    {/* End .entry-title */}
                    <div className="entry-cats">
                      in <a href="#">{item.category1}</a>,
                      <a href="#">{item.category2}</a>
                    </div>
                    {/* End .entry-cats */}
                    <div className="entry-content">
                      <p>{item.concept}</p>
                      <a href="single.html" className="read-more">
                        Continue Reading
                      </a>
                    </div>
                    {/* End .entry-content */}
                  </div>
                  {/* End .entry-body */}
                </article>
                {/* End .entry */}
              </div>
            );
          })}
        </div>
        {/* End .entry-container */}
      </div>
      <nav aria-label="Page navigation">
        <ul class="pagination justify-content-center">
          <li class="page-item disabled">
            <a
              class="page-link page-link-prev"
              href="#"
              aria-label="Previous"
              tabIndex="-1"
              aria-disabled="true"
            >
              <span aria-hidden="true">
                <i class="icon-long-arrow-left"></i>
              </span>
              Prev
            </a>
          </li>
          <li class="page-item active" aria-current="page">
            <a class="page-link" href="#">
              1
            </a>
          </li>
          <li class="page-item">
            <a class="page-link" href="#">
              2
            </a>
          </li>
          <li class="page-item">
            <a class="page-link page-link-next" href="#" aria-label="Next">
              Next{" "}
              <span aria-hidden="true">
                <i class="icon-long-arrow-right"></i>
              </span>
            </a>
          </li>
        </ul>
      </nav>
    </div>
  );
}
