import React from "react";
import { CardData } from "../../Data/CardData";

export default function NextProducts() {
  return (
    <div>
      <div
        className="owl-carousel owl-simple carousel-equal-height carousel-with-shadow"
        data-toggle="owl"
        data-owl-options='{
                            "nav": false, 
                            "dots": true,
                            "margin": 20,
                            "loop": false,
                            "responsive": {
                                "0": {
                                    "items":1
                                },
                                "480": {
                                    "items":2
                                },
                                "768": {
                                    "items":3
                                },
                                "992": {
                                    "items":4
                                },
                                "1200": {
                                    "items":4,
                                    "nav": true,
                                    "dots": false
                                }
                            }
                        }'
      >
        {CardData.map((item, index) => {
          return (
            <div key={index}>
              <div className="product product-11 text-center">
                <figure className="product-media">
                  <span class={`product-label label-circle ${item.style}`}>
                    {item.label}
                  </span>
                  <a href="product.html">
                    <img
                      src={item.img1}
                      alt="Product image"
                      className="product-image"
                    />
                    <img
                      src={item.img2}
                      alt="Product image"
                      className="product-image-hover"
                    />
                  </a>
                  <div className="product-action-vertical">
                    <a href="#" className="btn-product-icon btn-wishlist">
                      <span>add to wishlist</span>
                    </a>
                  </div>
                  {/* End .product-action-vertical */}
                </figure>
                {/* End .product-media */}
                <div className="product-body">
                  <h3 className="product-title">
                    <a href="product.html">{item.name}</a>
                  </h3>
                  {/* End .product-title */}
                  <div class="product-price">
                    <span class="new-price">{item.price1}</span>
                    <span class="old-price">{item.price2}</span>
                  </div>
                  {/* <!-- End .product-price --> */}

                  {/* End .product-price */}
                </div>
                {/* End .product-body */}
                <div className="product-action">
                  <a href="#" className="btn-product btn-cart">
                    <span>add to cart</span>
                  </a>
                </div>
                {/* End .product-action */}
              </div>
              {/* End .product */}
            </div>
          );
        })}
        {/* End .product */}
      </div>
      {/* End .owl-carousel */}
    </div>
  );
}
