import React from "react";
import { AccordianData } from "../../Data/AccordianData";
import { QuestionTitle } from "../../Data/QuestionsTitle";

export default function Question() {
  return (
    <div>
      {/* End .page-header */}
      <nav aria-label="breadcrumb" className="breadcrumb-nav">
        <div className="container another-container">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <a href="index.html">Home</a>
            </li>
            <li className="breadcrumb-item">
              <a href="#">Pages</a>
            </li>
            <li className="breadcrumb-item active" aria-current="page">
              FAQ
            </li>
          </ol>
        </div>
        {/* End .container */}
      </nav>
      {/* End .breadcrumb-nav */}

      <div className="page-content">
        <div className="container another-container">
          {/* Shipping Questions */}
          {QuestionTitle.map((item, index) => {
            return (
              <>
                <h2 className="title text-center mb-3" key={index}>
                  {item.question}
                </h2>
                {AccordianData.map((list) => {
                  return (
                    <div
                      className="accordion accordion-rounded"
                      id={list.accordianID}
                    >
                      <div className="card card-box card-sm bg-light">
                        <div className="card-header" id={list.headerID}>
                          <h2 className="card-title">
                            <a
                              role="button"
                              data-toggle="collapse"
                              href={list.href}
                              aria-expanded="false"
                              aria-controls={list.ariaControls}
                            >
                              {list.question}
                            </a>
                          </h2>
                        </div>
                        {/* End .card-header */}
                        <div
                          id={list.answerID}
                          className="collapse"
                          aria-labelledby={item.aria}
                          data-parent={item.data}
                        >
                          <div className="card-body">{item.answer}</div>
                          {/* End .card-body */}
                        </div>
                        {/* End .collapse */}
                      </div>
                      {/* End .card */}

                      {/* End .card */}
                    </div>
                  );
                })}
                ;
              </>
            );
          })}

          {/* Orders and Returns Questions */}
        </div>
        {/* End .container */}
      </div>
      {/* End .page-content --*/}
      <div
        className="cta cta-display bg-image pt-4 pb-4"
        style={{
          backgroundImage: "url(assets/images/backgrounds/cta/bg-7.jpg)",
        }}
      >
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-md-10 col-lg-9 col-xl-7">
              <div className="row no-gutters flex-column flex-sm-row align-items-sm-center">
                <div className="col">
                  <h3 className="cta-title text-white">
                    If You Have More Questions
                  </h3>
                  {/* End .cta-title */}
                  <p className="cta-desc text-white">
                    Quisque volutpat mattis eros
                  </p>
                  {/* End .cta-desc */}
                </div>
                {/* End .col */}
                <div className="col-auto">
                  <a href="/contact" className="btn btn-outline-white">
                    <span>CONTACT US</span>
                    <i className="icon-long-arrow-right" />
                  </a>
                </div>
                {/* End .col-auto */}
              </div>
              {/* End .row no-gutters */}
            </div>
            {/* End .col-md-10 col-lg-9 */}
          </div>
          {/* End .row */}
        </div>
        {/* End .container */}
      </div>
      {/* End .cta */}
      <div className="mb-4" />
    </div>
  );
}
